mapper.select:
SELECT
    r.id,
    r.name,
    r.description,
    r.cook_time AS cookTime,
    r.meal_type AS mealType,
    r.total_calories AS totalCalories
FROM recipe r
JOIN recipe_food rf ON r.id = rf.recipe_id
WHERE 1=1

    AND r.name LIKE CONCAT('%', #{name}, '%')


    AND r.cook_time  >=  #{cookTimeMin}


    AND r.cook_time  <=  #{cookTimeMax}


    AND r.total_calories  >=  #{caloriesMin}


    AND r.total_calories  <=  #{caloriesMax}


    AND r.meal_type = #{mealType}


    AND rf.food_id IN

        #{fid}


GROUP BY r.id

    HAVING COUNT(DISTINCT rf.food_id) = #{foodCount}

ORDER BY r.create_time DESC

SELECT COUNT(*)
FROM recipe_like
WHERE recipe_id = #{recipeId}
  AND user_id = #{userId}

mapper.delete:
DELETE FROM recipe_like
WHERE recipe_id = #{recipeId}
  AND user_id = #{userId}

mapper.update:
UPDATE recipe
SET like_count = like_count + 1
WHERE id = #{recipeId}

mapper.select:
SELECT like_count
FROM recipe
WHERE id = #{recipeId}

SELECT COUNT(1)
FROM recipe_favorite
WHERE recipe_id = #{recipeId} AND user_id = #{userId}

mapper.update:
UPDATE recipe
SET favorite_count = favorite_count + 1
WHERE id = #{recipeId}

mapper.select:
SELECT favorite_count
FROM recipe
WHERE id = #{recipeId}
